import React, { Component } from "react";
import "./App.css";
import Header from "./Components/Header";
// import Footer from "./Components/Footer";
import About from "./Components/About";
import Resume from "./Components/Resume";
// import Contact from './Components/Contact';
// import Testimonials from './Components/Testimonials';
// import Portfolio from './Components/Portfolio';

class App extends Component {
  state = {
    resumeData: {
      main: {
        name: "Tan Jia Jun",
        occupation: "Undergraduate",
        description:
          "I am open to new challenges within any industry and am willing to word hard to reach my goals."
          +"I am self motvatied and an excellent team player.",
        image: "profilepic.jpg",
        bio:
          "I am pursuing computer science in NTU and currently on my final semester of university. Looking for Full-Time/ Internship opportunity from May 2020 onward. " 
          +"I am specialise in Artificial Intelligence and Data Science. Did a few projects in the above mentioned field. " 
          +"I am also very interested in software engineering opportunities to integrate and expand my skill sets. ",
        contactmessage:
          "Here is where you should write your message to readers to have them get in contact with you.",
        email: "tanjiajun27@hotmail.com",
        phone: "+65 9779 3727",
        address: {
          street: "Toa Payoh",
          city: "Singapore",
          state: "Singapore",
          zip: "311014"
        },
        resumedownload:
          "https://github.com/jtan381/JiaJun_OnlineResume/raw/master/Resume.docx",
        social: [
          {
            name: "linkedin",
            url: "https://www.linkedin.com/in/jia-jun-tan-915283a9/",
            className: "fa fa-linkedin"
          },
          {
            name: "github",
            url: "https://github.com/jtan381",
            className: "fa fa-github"
          }
        ]
      },
      resume: {
        skillmessage:
          "Here you can create a short write-up of your skills to show off to employers",
        education: [
          {
            school: "Nanyang Technological University",
            qualification: "Bachelor of Engineering (Computer Science)",
            graduated: "Expected June 2020",
            description:
              "Describe your experience at school, what you learned, what useful skills you have acquired etc."
          },
          {
            school: "Ngee Ann Polytechnic",
            qualification: "Diploma in Electronic & Computer Engineering",
            graduated: "March 2003",
            description:
              "Enriched in intergrating both software and hardward aspect. Explored specific specialization on Network Systems & Security, trained on network configuration and security."
          }
        ],
        work: [
          {
            company: "Singapore Institute of Manufacturing Technology (SIMTech)",
            title: "Research and Development Engineer",
            years: "December 2019 - Present",
            description:
              "Describe work, special projects, notable achievements, what technologies you have been working with, and anything else that would be useful for an employer to know."
          },
          {
            company: "NTU SCSE",
            title: "Outreach Workshop Facilitator",
            years: "January 2018 - December 2019",
            description:
              "Facilitated JC and poly students capabilities of computational thinking, through hands-on RaspberryPi topped with SenseHAT and Pololu M3pi-robot. "
              +"Guided students through basic python programming knowledge."
          },
          {
            company: "Echo Tech Pte. Ltd",
            title: "Software Engineer",
            years: "September 2014 â€“ February 2015",
            description:
              "Developed program written in C# with SQL to extract data transmitted from RFID devices to calculate the distance and location for real time monitoring system. "
              + "Manage an IT infrastructure for all IT devices used in the same network throughout the project. "
              + "Wrote user manuals for training purpose on configure of device for co-worker and end users."
          }
        ],
        skills: [
          {
            name: "Git",
            level: "60%"
          },
          {
            name: "Python",
            level: "80%"
          },
          {
            name: "ReactJs",
            level: "70%"
          },
          {
            name: "CSS",
            level: "70%"
          },
          {
            name: "HTML5",
            level: "70%"
          },
          {
            name: "PostgreSQL",
            level: "60%"
          }
        ]
      }
    }
  };

  render() {
    return (
      <div className="App">
        {/* <Header todos={this.state.todos}/> */}
        <Header data={this.state.resumeData.main} />
        <About data={this.state.resumeData.main} />
        <Resume data={this.state.resumeData.resume} />
        {/* <Footer data = {this.state.resumeData.main}/> */}
      </div>
    );
  }
}

export default App;
